mapper.select:
SELECT
FROM pk_match t LEFT JOIN pk_team tm ON t.challengeteamid = tm.id LEFT JOIN pk_team tm2 ON t.hostteamid = tm2.id LEFT JOIN pk_park park ON t.parkid = park.id LEFT JOIN areas area ON park.area = area.id LEFT JOIN pk_member me ON t.initiatorid = me.id LEFT JOIN dict d on t.time = d.id where 1=1
AND area.id=#{areas}
AND t.status=#{pkstatus}
AND tm2.NAME like CONCAT('%',#{hostname},'%')
AND t.no=#{no}
LIMIT #{start}, #{size}
